function decodeUplink(input) {
   var bytes = input.bytes;
   var data = {};
   var i = 0;
   while (i < bytes.length) {
     switch (bytes[i]) {
       /*__________________________________________________________________________________________________
                                                     STATUS
          ____________________________________________________________________________________________________*/
       case 0x10:
         var channel = bytes[i + 1]; // get the channel
         switch (channel) {
           case 0x01: // battery
             data.battery = (bytes[i + 2] << 8) + bytes[i + 3]; // battery data of two bytes
             i += 4;
             break;
           case 0x02: // battery 9v
             data.battery_9V = (bytes[i + 2] << 8) + bytes[i + 3]; // battery 9v data of two bytes
             i += 4;
             break;
           case 0xf0: //  state
             data.rfu_value = bytes[i + 2]; // rfu data of one bytes
             data.states = bytes[i + 3]; // state of one byte (liebearth app will convert to binary)
             i += 4;
             break;
           default:
             break;
         }
         break;
 
       /*__________________________________________________________________________________________________
                                                     MOISTURE
          ____________________________________________________________________________________________________*/
       case 0x20:
         var channel = bytes[i + 1]; // get the channel of one byte
         switch (channel) {
           case 0x01: // moisture_1 data  of two bytes
             data.moisture_1 = (bytes[i + 2] << 8) + bytes[i + 3];
             i += 4;
             break;
 
           case 0x02: // moisture_2 data  of two bytes
             data.moisture_2 = (bytes[i + 2] << 8) + bytes[i + 3];
             i += 4;
             break;
 
           default:
             break;
         }
         break;
 
       /*__________________________________________________________________________________________________
                                                     TEMPERATURE
          ____________________________________________________________________________________________________*/
       case 0x30:
         break;
 
       /*__________________________________________________________________________________________________
                                                        FLOW
          ____________________________________________________________________________________________________*/
       case 0x40:
         break;
 
       /*__________________________________________________________________________________________________
                                                        PIR
          ____________________________________________________________________________________________________*/
       case 0x50:
         break;
 
       /*__________________________________________________________________________________________________
                                                        HOUR/MIN
          ____________________________________________________________________________________________________*/
       case 0x91:
         break;
 
       /*__________________________________________________________________________________________________
                                                           HUPLINK PERIOD
          ____________________________________________________________________________________________________*/
       case 0xa0:
         break;
 
       /*__________________________________________________________________________________________________
                                                           ONLINE WHEN
          ____________________________________________________________________________________________________*/
       case 0xb0:
         break;
 
       /*__________________________________________________________________________________________________
                                                           ONLINE DURATION
          ____________________________________________________________________________________________________*/
       case 0xb2:
         break;
 
       /*__________________________________________________________________________________________________
                                                           OFFLINE WHEN
          ____________________________________________________________________________________________________*/
       case 0xb8:
         break;
 
       /*__________________________________________________________________________________________________
                                                           OFFLINE DURATION
          ____________________________________________________________________________________________________*/
       case 0xb9:
         break;
 
       /*__________________________________________________________________________________________________
                                                           DEFAULT
          ____________________________________________________________________________________________________*/
       default:
         data = {};
         i = len;
         break;
     }
   }
   var output =  {
     data:data,
     errors:[],
     warnings:[],
   }
   return output;
 }
 
 exports.decodeUplink = decodeUplink;
